import React, { useState } from 'react'

import Member from './Member'
import MemberList from './MemberList'
import CrewList from './CrewList'

function CrewMaker() {
    const [ memberList, updateMemberList ] = useState([
        ["Jenna", false],
        ["Sophie", false],
        ["Grace",false],
        ["Isobel",false],
        ["Chloe",false],
        ["Kiwa",false],
        ["Kate",false]
    ]) 
    const [selected, updateSelected ] = useState([])
    const [crews, updateCrews ] = useState([{id: 1, members: []}])

    function handleSelect(member_name){
        my_selected = selected.map(i => i)
        var present = false
        for(var i=0; i< my_selected.length();i++){
            if(my_selected[i][0] == member_name){
                my_selected.splice(i,1)
                present = true
            }
        }
        !present ? my_selected.push([member_name, true]) : false
        
        updateSelected(my_selected)
    }
    function addMembers(crew_id){
        //Attempt adding selected to crews
        const my_crews = crews.map(item => item)
        my_crews.forEach(crew => {
            if(crew.id == crew_id){
                selected.forEach(name => {
                    if(!crew.members.includes(name)){
                        crew.members.push(name)
                    }
                })
            }
        })
        //Add selected to given crew
        updateCrews(my_crews)

        //Get selected out of memberlist
        selected.forEach(item => {
            updateMemberList(prevState => prevState.filter(member => {
                return member !== item
            }))
        })
        
        
        updateSelected([])
    }

    
    return (
        <div className="content-wrapper">
            <MemberList members={memberList} memberclick={handleSelect}/>
            <CrewList crews={crews} callback={addMembers} memberclick={handleSelect}/>
        </div>
    )
}

export default CrewMaker
