import React, { Component } from 'react';


class App extends Component {
	constructor() {
		super()
		this.state = {
			search: "",
			searchTerms: [
				"Apples",
				"Bananas",
				"Bath Salts",
				"Bicycle",
				"Rhino",
				"Table",
				"Terrace",
				"Yellow",
				"Young",
				"Zebra"
			],
			matchingTerms: [],
			characters: {},
			loading: true
		}
		this.handleEvent = this.handleEvent.bind(this)
		this.searchData = this.searchData.bind(this)
	}

	searchData(val) {
		this.setState({ loading: true })
		fetch("https://swapi.co/api/people/?search=" + val)
			.then(response => response.json())
			.then(data => {
				this.setState({
					characters: data.results,
					loading: false

				})
			})
	}

	handleEvent(event) {
		let { name, value } = event.target
		if (name === "searchbar") {
			this.setState({
				search: value,
				matchingTerms: searchArray(this.state.searchTerms, value)
			})
			this.searchData(value)

		}
	}

	render() {
		let myList = this.state.matchingTerms.map(term => <p>{term}</p>)
		// let starList = this.state.characters.map(item => item.name)

		return (
			<div>
				<input
					name="searchbar"
					type="text"
					value={this.state.search}
					onChange={this.handleEvent}>
				</input>

				<h3>{this.state.search}</h3>
				{myList}

				<div>
					<h1>Characters</h1>
					<div>{this.state.loading ? "Loading..." : console.log(this.state.characters)}</div>
				</div>
			</div>
		);
	}
}

function searchArray(arr, val) {
	if (val == "") {
		return []
	}
	return arr.filter(term => term.substring(0, val.length) == val)
}

export default App;
