import React, {useState, useEffect} from 'react'
import WeightIcon from '../images/weights.png'

function SidePanel(props){

    const [squads, updateSquads] = useState([])


    const squad_names = props.squads.map((squad,i) =>{
        return <p 
                    key={i} 
                    onClick={() => props.updateSquad(squad)}
                    className={props.currentSquad === squad ? "list-active": ""}
                >
                    {squad}
                </p>
    })
    return(
        <div className="side-panel">
            <div className="side-panel-list">
                <h2 className="active" onClick={() => props.updateSquad("all")}>
                    <img src={WeightIcon} className="list-icon"></img>
                    Trainings
                </h2>
                <div className="side-panel-sublist">
                    {squad_names}
                </div>
            </div>

            <div className="side-panel-list">
                <h2>Regattas</h2>
            </div>

            <div className="side-panel-list">
                <h2>Events</h2>
            </div>
        </div>
    )
}
export default SidePanel
