import React from 'react'
import getOrdinals from '../scripts/getOrdinals'

function DateItem(props) {
    function handleClick(e){
        //Change page to crew maker
        props.pageupdate("crew-maker")
    }
    var colorCode = "#fcba03"
    if(props.colour === "blue")colorCode = "#483bff"
    else if(props.colour === "red")colorCode = "#e22113"
    else if(props.colour === "green")colorCode = "#46dd0b"
    else if(props.colour === "yellow")colorCode = "#ffeb3b"

    //Turn date into Day of the Week
    const day = new Date(props.date)
    const days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"]
    const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September","October", "November", "December"]
    return(
        <div className="date-item" onClick={handleClick}>
            <div className="color-code" style={{"background": colorCode}}></div>
            <div className="date-content">
                <p>{`${days[day.getDay()-1]} ${day.getDate()}`}<sup>{getOrdinals(day.getDate())}</sup> {`${months[day.getMonth()]}`} <span className="squad-name-sm"></span></p>
                <p>{props.name}</p>
                <p>{props.location}</p>
            </div>
        </div>
    )
}

export default DateItem
