//Returns true if date 1 is the same week as date 2
function getWeek(date1,date2) {
    date1 = new Date(date1)
    date2 = new Date(date2)

    if(date1.getWeek() === date2.getWeek()){
        return true
    }
    else {
        return false
    }

}

module.exports = getWeek
