import React from 'react';


class App extends React.Component {
	constructor() {
		super()
		this.state = {
			search: ""
		}
		this.handleEvent = this.handleEvent.bind(this)
	}

	handleEvent(event){
		let {name,value} = event.target
		console.log(name, value)
	}

	render() {
		return (
			<div>
				<input
					type="text"
					value=""
					onChange={this.handleEvent}>
				</input>
			</div>
		);
	}
}

export default App;
