import React from 'react'

import DateItem from './DateItem'

function MainPanel(props){

    function handleClick(e){
        props.handleClick(e.target.name)
    }
    const trainingList = props.trainings.map((training,i) => {
        return(
            <DateItem 
                key={i}
                date={training.date}
                pageupdate={props.pageupdate}
            />
        )
    })
    return(
        <div className="main-panel">
            <div className="dates-list-container">
                <h1>Upcoming Training</h1>
                <div className ="create-training-button" name="create-training"onClick={handleClick}><p>Create Training</p></div>
                <div className="dates-list rounded-top">
                    <h3>Today</h3>
                    {trainingList}
                </div>
            </div>
            <div className="info-panel-container ">
                <h1>Crews</h1>
                <div className="info-panel rounded-top">

                </div>
            </div>
            
        </div>
    )
}
export default MainPanel
