import React, { useState, useEffect } from 'react'
import '../styles/calendar.css'

import SidePanel from './SidePanel'
import MainPanel from './MainPanel'
import CreateTraining from './CreateTraining'

function Calendar(props) {
    const [createTraining, updateCreateTraining] = useState(false)
    const [currentSquad, updateCurrentSquad] = useState("all")
    const [ squads, updateSquads ] = useState([])
    const [trainings, updateTrainings] = useState([])


    useEffect(() => {
        fetch('/api/squads')
            .then(res => res.json())
            .then(data => updateSquads(data))

    }, [])
    //Update when createTraining window closes
    useEffect(() => {
        fetch(`/api/training/?squad=${currentSquad}`)
            .then(res => res.json())
            .then(data => {
                updateTrainings(data)
            })
    }, [createTraining])

    //Update when currentSquad changes
    useEffect(() => {
        fetch(`/api/training/?squad=${currentSquad}`)
            .then(res => res.json())
            .then(data => {
                updateTrainings(data)
            })
    }, [currentSquad])

    function handleClick(name) {
        if (name === "create-training") {
            updateCreateTraining(true)
        }
    }


    return (
        // style={{backgroundImage: `url(${Background})`}}
        <div className="calendar-wrapper" >
            <SidePanel updateSquad={updateCurrentSquad} squads={squads}/>
            <div></div>
            <MainPanel trainings={trainings} pageupdate={props.pageupdate} handleClick={handleClick}/>
            {createTraining ? <CreateTraining display={updateCreateTraining} squad={currentSquad} squads={squads} /> : false}
        </div>
    )
}
export default Calendar
