import React from 'react';


class App extends React.Component {
	constructor() {
		super()
		this.state = {
			search: "",
			searchTerms: [
				"Apples",
				"Bananas",
				"Bath Salts",
				"Bicycle",
				"Rhino",
				"Table",
				"Terrace",
				"Yellow",
				"Young",
				"Zebra"
			],
			matchingTerms: []
		}
		this.handleEvent = this.handleEvent.bind(this)
	}

	componentDidMount(){
		fetch("https://swapi.co/api/people")
            .then(response => response.json())
            .then(data => {
                this.setState({
                    character: data
                })
            })
	}

	handleEvent(event){
		let {name,value} = event.target
		if(name === "searchbar"){
			this.setState({
				search: value,
				matchingTerms: searchArray(this.state.searchTerms, value)
			})

		}
	}

	render() {
		let myList = this.state.matchingTerms.map(term => <p>{term}</p>)
		return (
			<div>
				<input
					name="searchbar"
					type="text"
					value={this.state.search}
					onChange={this.handleEvent}>
				</input>

				<h3>{this.state.search}</h3>
				{myList}
			</div>
		);
	}
}

function searchArray(arr, val){
	if(val == ""){
		return []
	}
	return arr.filter(term => term.substring(0,val.length) == val)
}

export default App;
