function getOrdinals(num) {
  switch (num) {
    case 1 || 21 || 31:
      return "st";

    case (2, 22):
      return "nd";

    case (3, 23):
      return "rd";

    case num >= 4 && num <= 20:
      return "th";

    case (24, 25, 26, 27, 28, 29, 30):
      return "th";
  }
}

module.exports = getOrdinals;
