import React from 'react';


class App extends React.Component {
	constructor() {
		super()
		this.state = {
			search: "",
			searchTerms: [
				"Apples",
				"Bananas",
				"Bath Salts",
				"Bicycle",
				"Rhino",
				"Table",
				"Terrace",
				"Yellow",
				"Young",
				"Zebra"
			]
		}
		this.handleEvent = this.handleEvent.bind(this)
	}

	handleEvent(event){
		let {name,value} = event.target
		if(name === "searchbar"){
			this.setState({search: value})
			searchArray(this.state.searchTerms, value)

		}
	}

	render() {
		return (
			<div>
				<input
					name="searchbar"
					type="text"
					value={this.state.search}
					onChange={this.handleEvent}>
				</input>

				<h3>{this.state.search}</h3>
			</div>
		);
	}
}

function searchArray(arr, term){
