//Returns true if date 1 is the same week as date 2
function getWeek(date1,date2) {
    let date_1 = new Date(date1)
    let date_2 = new Date(date2)

    if(date_1.getDay() === date_2.getDay()){
        return true
    }
    else {
        return false
    }

}

module.exports = getWeek
