import React from 'react'
import getOrdinals from '../scripts/getOrdinals'

import DateItem from './DateItem'

function MainPanel(props) {

    const trainingList = props.trainings.map((training, i) => {
        return (
            <DateItem
                key={i}
                name={training.name}
                date={training.date}
                colour={training.colour}
                location={training.location}
                squad={training.squad}
                onClick={() => {
                    props.handleClick(
                        "current-training",
                        {
                            name: training.name,
                            date: training.date,
                            desc: training.description,
                            location: training.location
                        }
                    )
                }}
            />
        )
    })
    let date = "No Date Found"
    if(props.currentTraining.date){
        date = new Date(props.currentTraining.date)
    }
    return (
        <div className="main-panel">
            <div className="dates-list-container">
                <h1>Upcoming Training</h1>
                <div
                    className="create-training-button"
                    name="create-training"
                    onClick={(e) => props.handleClick("create-training")}
                >
                    <p>Create Training</p>
                </div>
                <div className="dates-list rounded-top">
                    <h3>Today</h3>
                    {trainingList}
                </div>
            </div>
            <div className="info-panel-container ">
                <h1>Crews</h1>
                <div className="info-panel rounded-top">

                    {!props.currentTraining.name ? "No training chosen to display" :
                        <div>
                            <div className="info-panel-head">
                                <h1>{props.currentTraining.name}</h1>
                                <p>{
                                    `
                                    date.getDate()${getOrdinals(date.getDate())}
                                    `
                                    }</p>
                            </div>
                            <p>{props.currentTraining.desc}</p>
                        </div>
                    }

                </div>
            </div>

        </div>
    )
}
export default MainPanel
