import React, {useState} from 'react'
import Member from './Member'

function Crew(props){
    const [crewMembers, updateCrewMembers ] = useState([])
    function addMembers(){
        console.log("adding Members")
        updateCrewMembers((prevState) => [...prevState, props.selected])
    }
    return(
        <div onClick={addMembers} className="crew">
            <p>{props.id}</p>
            {console.log(crewMembers)}
            {crewMembers.map(member => <Member name={member}/>)}
        </div>
    )
}
export default Crew
